import React from "react";
import StarRating from "./starrating";



export default class ReviewForm extends React.Component {

    constructor(props){
        super(props);

        this.state = {
            review: {
                name: props.name,
                review: props.review,
                rating: <StarRating />
            }
        }
    }
    handleNameChanged(event) {
        var review = this.state.review;
        var modifiedValue = event.target.value;
        review.name = modifiedValue;
        this.setState({
          review:review
        });
    }
    handleReviewChanged(event) {
        var review = this.state.review;
        var modifiedValue = event.target.value;
        review.review = modifiedValue;
        this.setState({
            review:review
        });
    }

    handleRatingChanged(event) {
        var review = this.state.review;
        var modifiedValue = event.target.value;
        review.rating = modifiedValue;
        this.setState({
            review:review
        });
    }
        handleButtonClicked() {
            console.log(this.state.review);
          }

    render(){

        return(


        <div>
            <label>
                Name:
            </label>
            <input type='text' value={this.state.review.name} onChange = {this.handleNameChanged.bind(this)}/>
            <br/>
            <label>
                Review:
            </label>
            <input type='text' value={this.state.review.review} onChange = {this.handleReviewChanged.bind(this)}/>
            <br/>
            <label>
                Rating:
            </label>
            <StarRating value={this.state.review.rating} onChange = {this.handleRatingChanged.bind(this)}/>
            <br/>
            <button type="submit" className ="btn btn-info" onClick={this.handleButtonClicked.bind(this)}>Submit Review</button>

        </div>
   ); 
};
}
